export function combine(path, excludeFileName){
    const path1 = '@/i18n/en'
    const moduleFiles = require.context(path1, true, /\.js$/);

    const modules = moduleFiles.keys().reduce((modules, modulePath) => {
        const moduleName = modulePath.replace(/^\.\/(.*)\.\w+$/, '$1')
        if(excludeFileName && moduleName != excludeFileName){
            const value = moduleFiles(modulePath)
            return Object.assign({}, modules, value.default)
        } else {
            return modules;
        }
    }, {})

    return modules;
}
